import {useState} from 'react'
import './TableRow.css'
const TableRow = ({ user, key, info, setInfo }) => {
    const [check, setCheck] = useState(false)

    if(key === info){ setCheck(true) }
    const changeInfo = key => setInfo(key)
    return (
        check?
                <div className="TableRowWrapper Selected">
                    <span className="TableRowName">{user.name}</span>
                    <span className="TableRowGrade">{user.grade}</span>
                    <span className="TableRowDetail" onClick={() => changeInfo(key)}><div className="pointLeft"></div></span>
                </div>
                :
                <div className="TableRowWrapper">
                    <span className="TableRowName">{user.name}</span>
                    <span className="TableRowGrade">{user.grade}</span>
                    <span className="TableRowDetail" onClick={() => changeInfo(info)}><div className="pointRight"></div></span>
                </div>




    )
}




export default TableRow
